import jQuery from "jquery";
jQuery(function () {
  // Admin Panel settings

  //****************************
  /* This is for the mini-sidebar if width is less then 1170*/
  //****************************
  var setsidebartype = function () {
    var width =
      window.innerWidth > 0 ? window.innerWidth : this.screen.width;
    if (width < 1199) {
      jQuery("#main-wrapper").attr("data-sidebartype", "mini-sidebar");
      jQuery("#main-wrapper").addClass("mini-sidebar");
    } else {
      jQuery("#main-wrapper").attr("data-sidebartype", "full");
      jQuery("#main-wrapper").removeClass("mini-sidebar");
    }
  };
  jQuery(window).ready(setsidebartype);
  jQuery(window).on("resize", setsidebartype);
  //****************************
  /* This is for sidebartoggler*/
  //****************************
  jQuery(".sidebartoggler").on("click", function () {
    jQuery("#main-wrapper").toggleClass("mini-sidebar");
    if (jQuery("#main-wrapper").hasClass("mini-sidebar")) {
      jQuery(".sidebartoggler").prop("checked", !0);
      jQuery("#main-wrapper").attr("data-sidebartype", "mini-sidebar");
    } else {
      jQuery(".sidebartoggler").prop("checked", !1);
      jQuery("#main-wrapper").attr("data-sidebartype", "full");
    }
  });
  jQuery(".sidebartoggler").on("click", function () {
    jQuery("#main-wrapper").toggleClass("show-sidebar");
  });
})